CREATE FUNCTION `SPLIT_STRING`(str VARCHAR(255) ,
  delim VARCHAR(12) ,
  pos INT
) RETURNS varchar(255) CHARSET utf8mb3 COLLATE utf8mb3_general_ci
    DETERMINISTIC
BEGIN 
  RETURN
    REPLACE(
      SUBSTRING(
        SUBSTRING_INDEX(str , delim , pos) ,
        CHAR_LENGTH(
          SUBSTRING_INDEX(str , delim , pos - 1)
        ) + 1
      ) ,
      delim , ''
    );
END ;;
DELIMITER ;
