CREATE PROCEDURE `add_alias`(
  IN _ident VARCHAR(80),
  IN _alias VARCHAR(512)
)
BEGIN
  INSERT INTO alias VALUES (_ident, _alias, 'user', SUBSTRING_INDEX(_alias, '.', -2))
  ON DUPLICATE KEY UPDATE vhost=_alias;
