CREATE PROCEDURE `cleanup_sessions`(
  IN session_ttl INT(11)
)
BEGIN

  DELETE FROM sessions  WHERE cast((UNIX_TIMESTAMP() - update_time) as SIGNED) > session_ttl;

END ;;
