CREATE PROCEDURE `dmz_notify_list_next`(
  IN _nid VARCHAR(50), _flag   VARCHAR(50)
)
BEGIN
  DECLARE _ts INT(11);
  
  SELECT UNIX_TIMESTAMP()INTO _ts;
  SELECT UNIX_TIMESTAMP()-43200 INTO _ts WHERE _flag = 'new' ;
  

  SELECT 
    hub_id,
    id ,
    email,
    notify_at
  FROM 
    yp.dmz_userx s
  WHERE s.nid = _nid AND  s.notify_at <= _ts ;
END ;;
