CREATE PROCEDURE `drumate_check`(
  IN _id          VARCHAR(160)
)
BEGIN
  DECLARE _drumate_id VARBINARY(16);
  SELECT id FROM drumate WHERE id = _id OR email = _id INTO _drumate_id;
  IF (IFNULL(_drumate_id, "") = "") THEN
    SELECT 0 AS drumate;
  ELSE
    SELECT 1 AS drumate;
  END IF;
END ;;
