CREATE PROCEDURE `get_frozen_entities`()
BEGIN
  SELECT id, ident, fs_host, home_dir, db_name, type FROM entity WHERE status = 'frozen' AND UNIX_TIMESTAMP(TIMESTAMPADD(DAY,90, FROM_UNIXTIME(frozen_time))) < UNIX_TIMESTAMP();
END ;;
DELIMITER ;
