CREATE PROCEDURE `log_activity`(
  IN _uid      varchar(16),
  IN _username varchar(40),
  IN _ip       varchar(40),
  IN _ip_fwd   varchar(40),
  IN _req_uri  varchar(255),
  IN _referer  varchar(255),
  IN _ua       varchar(255),
  IN _action   varchar(40)
)
BEGIN

  INSERT INTO log values(
    sha2(uuid(),224),
    sha2(uuid(),224),
    _uid,
    _username,
    UNIX_TIMESTAMP(),
    UNIX_TIMESTAMP(),
    0,
    _ip,
    _ip_fwd,
    _req_uri,
    _referer,
    _ua,
    _action
  );
END ;;
