CREATE PROCEDURE `otp_check`(
  IN _uid VARCHAR(16),
  IN _secret VARCHAR(64), 
  IN _code INT(10)
)
BEGIN
  DELETE FROM otp WHERE UNIX_TIMESTAMP() - ctime > 60*30;
  SELECT *, ctime + 60*30 expiry FROM otp WHERE `uid`=_uid 
    AND `secret`=_secret AND `code`=_code;
END ;;
