CREATE PROCEDURE `session_logout`(
  IN _key VARCHAR(128),
  IN _uid VARCHAR(16)
)
BEGIN
  DELETE FROM cookie  WHERE id=_key AND `uid`=_uid;
  DELETE FROM socket  WHERE cookie=_key AND `uid`=_uid;
  DELETE FROM cookie WHERE UNIX_TIMESTAMP() - mtime > 7*24*60*60;
END ;;
