CREATE PROCEDURE `socket_user_connections`(
  IN _key VARCHAR(80) CHARACTER SET ascii 
)
BEGIN
  
  
  
  SELECT 
    1 isDrumate,
    s.id AS connection_id,
    s.id AS socket_id,
    e.id AS user_id,
    d.username as ident,
    s.server,
    e.db_name,
    home_dir,
    remit,
    d.lang,
    d.avatar,
    e.status,
    d.profile,
    e.settings,
    
    
    fullname
    FROM entity e 
      INNER JOIN drumate d ON e.id=d.id 
      INNER JOIN socket s  ON e.id=s.uid 
      WHERE e.id=_key AND s.state = 'active';
  
END ;;
