CREATE PROCEDURE `yp_find_country`(
  IN _pattern VARCHAR(84)
)
BEGIN

  select *, MATCH(`fr`, `en`) against(concat(_pattern, '*') IN BOOLEAN MODE) as relevance
  from country having relevance > 1 ORDER BY relevance DESC LIMIT 30;

END ;;
