CREATE PROCEDURE `yp_find_language`(
  IN _pattern VARCHAR(84)
)
BEGIN

  select *, if(locale='', locale_en, concat (locale_en, ' - ', locale)) as content
  from `language` WHERE locale LIKE concat("%", _pattern, "%") or locale_en LIKE concat("%", _pattern, "%")
  LIMIT 20;

END ;;
